    var czml = [
      {
        id: "document",
        version: "1.0",
        clock: {
          interval: "2018-07-19T15:18:00Z/2018-07-19T15:18:30Z",
          currentTime: "2018-07-19T15:18:00Z",
          multiplier: 0.5,
          range: "LOOP_STOP",
          step: "SYSTEM_CLOCK_MULTIPLIER",
        },
      },
      {
        id: "CesiumMilkTruck",
        model: {
          gltf: "./models/Cesium_Man.gltf",
          scale: 200,
        },
        position: {
          interpolationAlgorithm: "LINEAR",
          forwardExtrapolationType: "HOLD",
          cartesian: [
            "2018-07-19T15:18:00Z",
            1216348.1632364073,
            -4736348.958775471,
            4081284.5528982095,
            "2018-07-19T15:18:30Z",
            1216369.1229444197,
            -4736377.467107148,
            4081240.888485707,
          ],
        },
        orientation: {
          unitQuaternion: [
            0.3084011337938999,
            0.3210181022701266,
            -0.45850421987074924,
            0.7686388857813198,
          ],
        },
      },
      {
        id: "Polyline",
        polyline: {
          positions: {
            cartesian: [
              1216348.1632364073,
              -4736348.958775471,
              4081284.5528982095,
              1216369.1229444197,
              -4736377.467107148,
              4081240.888485707,
            ],
          },
          material: {
            polylineOutline: {
              color: {
                rgba: [255, 255, 0, 255],
              },
              outlineColor: {
                rgba: [0, 0, 0, 255],
              },
              outlineWidth: 2,
            },
          },
          width: 10,
          clampToGround: true,
        },
      },
    ];

    var viewer = new Cesium.Viewer("map", {
      terrainProvider: Cesium.createWorldTerrain(),
    });
    var scene = viewer.scene;
    var clock = viewer.clock;
    var entity;
    var positionProperty;
    var dataSourcePromise = Cesium.CzmlDataSource.load(czml);
    viewer.dataSources.add(dataSourcePromise).then(function (dataSource) {
      entity = dataSource.entities.getById("CesiumMilkTruck");
      positionProperty = entity.position;
    });

    var tileset = scene.primitives.add(
      new Cesium.Cesium3DTileset({
        url: Cesium.IonResource.fromAssetId(40866),
      })
    );

    viewer.camera.setView({
      destination: new Cesium.Cartesian3(
        1216403.8845586285,
        -4736357.493351395,
        4081299.715698949
      ),
      orientation: new Cesium.HeadingPitchRoll(
        4.2892217081808806,
        -0.4799070147502502,
        6.279789177843313
      ),
      endTransform: Cesium.Matrix4.IDENTITY,
    });

    if (scene.clampToHeightSupported) {
      tileset.initialTilesLoaded.addEventListener(start);
    } else {
      window.alert("This browser does not support clampToHeight.");
    }

    function start() {
      clock.shouldAnimate = true;
      var objectsToExclude = [entity];
      scene.postRender.addEventListener(function () {
        var position = positionProperty.getValue(clock.currentTime);
        entity.position = scene.clampToHeight(position, objectsToExclude);
      });
    }
  },
};
</script>

